mapper.insert:

	SELECT nextval('BOARD_SEQ') FROM DUAL	/*boardSeq 값을 map에 반환*/

INSERT 
  INTO TB_PLAY					/*테이블_놀이*/
     (
       BOARD_SEQ                /*게시글일련번호*/ 
     , TITLE                    /*자유게시판제목*/ 
     , INTRO                    /*소개*/
     , CN                       /*자유게시판내용*/ 
     , FST_REG_ID 				/*최초등록자ID*/
     , LT_UPD_ID 				/*최종수정자ID*/
     )
VALUES
	 (
	   #{boardSeq}	
	 , #{title}	
	 , #{intro}
	 , #{cn}
     , #{loginInfo.userId} 	
     , #{loginInfo.userId}		 
	 )
