mapper.select:
SELECT T1.MNU_SEQ /*메뉴일련번호*/ , T1.URL /*메뉴경로*/ , T1.MNU_NM /*메뉴명*/ , T1.TOP_URL /*최상위메뉴경로*/ , T1.UPPER_URL /*상위메뉴경로*/ , T1.OPEN_YN /*메뉴노출여부*/ , T1.AUTH_YN /*권한검사여부*/ , T1.MNU_LV /*메뉴레벨*/ , T1.INFO /*메뉴정보*/ , T1.MNU_ORDER /*메뉴표시순서*/ , T1.RMRK /*메뉴비고*/ , IF(T1.MNU_LV = 1 , T1.MNU_ORDER * 1000 , (SELECT MNU_ORDER * 1000 + T1.MNU_ORDER FROM TB_MNU WHERE URL = T1.UPPER_URL) ) AS orderVal /*메뉴순서 값(lv1 order * 1000 + lv2 order)*/ FROM TB_MNU T1 /*테이블_권한그룹*/ WHERE 1 = 1
AND T1.MNU_SEQ = #{mnuSeq}
ORDER BY orderVal ASC
