mapper.select:
SELECT COUNT(*) OVER() AS ROW_CNT /*전체 레코드 수(페이징)*/ , T1.BOARD_SEQ /*게시글일련번호*/ , T1.TITLE /*자유게시판제목*/ , T1.CN /*자유게시판내용*/ , T1.FREE_CODE /*자유게시판코드*/ , T1.HIT /*자유게시판조회수*/ , T1.FST_REG_ID /*최초등록자아이디*/ , DATE_FORMAT(T1.FST_REG_DTTI, "%Y/%m/%d %H:%i") FST_REG_DTTI /*최초등록일시*/ , T1.LT_UPD_ID /*최종수정자아이디*/ , DATE_FORMAT(T1.LT_UPD_DTTI, "%Y/%m/%d %H:%i") LT_UPD_DTTI /*최종수정일시*/ FROM TB_BOARD_FREE T1 /*테이블_자유게시판*/ WHERE 1 = 1
AND T1.BOARD_SEQ = ${boardSeq}
ORDER BY T1.LT_UPD_DTTI DESC
LIMIT ${strIdx}, ${pageLength}
