mapper.insert:

	SELECT nextval('BOARD_SEQ') FROM DUAL

INSERT 
  INTO TB_BOARD_NOTICE			/*테이블_공지사항*/
     (
       BOARD_SEQ				/*게시글일련번호*/
     , TITLE 					/*공지사항제목*/
     , CN						/*공지사항내용*/
     , STR_DT					/*공지사항게시시작일*/
     , END_DT					/*공지사항게시종료일*/
     , POP_YN					/*공지사항팝업여부*/
     , FST_REG_ID 				/*최초등록자ID*/
     , LT_UPD_ID 				/*최종수정자ID*/
     )
VALUES
	 (
	   #{boardSeq}	
	 , #{title}	
	 , #{cn}
	 , #{strDt}
	 , #{endDt}
	 , #{popYn}
     , #{loginInfo.userId} 	
     , #{loginInfo.userId}		 
	 )
