mapper.select:
SELECT COUNT(*) OVER() AS ROW_CNT /*전체 레코드 수(페이징)*/ , T1.ENT_MARKET_SEQ /*기업장터일련번호*/ , T1.PRO_NAME /*상품명*/ , T1.PRICE /*가격*/ , T1.CNT /*재고수량*/ , T1.CN /*상세내용*/ , T1.MARKET_TYPE_CODE /*상품분류코드(01:의류,02:장난감:03:생활용품)*/ , T1.THUMBNAIL /*썸네일 파일명*/ , T1.USE_YN /*사용여부*/ , T1.FST_REG_ID /*최초등록자아이디*/ , DATE_FORMAT(T1.FST_REG_DTTI, "%Y/%m/%d %H:%i") FST_REG_DTTI /*최초등록일시*/ , T1.LT_UPD_ID /*최종수정자아이디*/ , DATE_FORMAT(T1.LT_UPD_DTTI, "%Y/%m/%d %H:%i") LT_UPD_DTTI /*최종수정일시*/ FROM TB_ENT_MARKET T1 /*테이블_기업장터*/ WHERE 1 = 1 AND T1.USE_YN = 'Y'
AND T1.ENT_MARKET_SEQ = ${entMarketSeq}
LIMIT ${strIdx}, ${pageLength}

mapper.insert:

	SELECT nextval('ENT_MARKET_SEQ') FROM DUAL	/*sequence 값을 map에 반환*/

INSERT 
  INTO TB_ENT_MARKET			/*테이블_기업장터*/
     (
       ENT_MARKET_SEQ          	/*기업장터일련번호*/
     , PRO_NAME                	/*상품명*/
     , PRICE                   	/*가격*/
     , CNT                     	/*재고수량*/
     , CN                      	/*상세내용*/
     , MARKET_TYPE_CODE        	/*상품분류코드*/
     , THUMBNAIL               	/*썸네일*/
     , FST_REG_ID 				/*최초등록자ID*/
     , LT_UPD_ID 				/*최종수정자ID*/
     )
VALUES
	 (
	   #{entMarketSeq}
	 , #{proName}
	 , #{price}
	 , #{cnt}
	 , #{cn}
	 , #{marketTypeCode}
	 , #{thumbnail}
     , #{loginInfo.userId} 	
     , #{loginInfo.userId}		 
	 )
